import React, { Component } from 'react'
//import { Link } from 'react-router-dom'
import axios from 'axios';
//import AuthenticationForApiService from './AuthenticationForApiService.js'
//import {PROPERTY_URL} from '../../constants/constants'

class FrontPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            location: '',
            endDate: '',
            startDate:'',
            sharingType:'entirePlace',
            propertyType:'',
            propertyDescription: '',
            wifi : '',
            priceRange : ''
        }
        this.ChangeHandler = this.ChangeHandler.bind(this);
        this.SearchButton = this.SearchButton.bind(this);
    }

    componentWillMount() {

    }

    SearchButton = (e) => {
        //var headers = new Headers();
        e.preventDefault();
        if(this.state.location === "")
            alert("Address is Empty");
        else{
            const data = {
                city : this.state.location,
                endDate : this.state.endDate,
                startDate : this.state.startDate,
                sharingType : this.state.sharingType,
                propertyType: this.state.propertyType,
                propertyDescription: this.state.propertyDescription,
                wifi : this.state.wifi,
                priceRange : this.state.priceRange
            }
            console.log(data);
            // axios.defaults.withCredentials = true;
      //      axios.get(`${PROPERTY_URL}/search/property`,data)
            axios.post(`http://localhost:8181/search/property`,data)
                .then(response => {
                    console.log("Status Code : ",response.status);
                    if(response.status === 200){
                        // this.setState({
                        //     flag : true,
                        //     responseData:response.data,
                        //     startDate:stringStartDate,
                        //     endDate:stringEndDate
                        // })
                        console.log(response);
                        if(!response.data){
                            alert("No Available Properties")
                        }
                    }
                    else{
                        this.setState({
                            flag : false
                        })
                    }
                })
                .catch(err =>{
                    alert(err);
                });


        }
    }

    handleChange(event) {
        this.setState({value: event.target.value});
      }

    ChangeHandler(e) {
        let change = {}
        change[e.target.name] = e.target.value
        this.setState(change)
    }

    render() {
        // console.log(this.state.location);
        // console.log(this.state.startDate);
         console.log(this.state.sharingType);
        return (
            <div>
                <div class="container-fluid">
                    <br />
                    <br />
                    <div class="row" >
                        <div class="col-sm-1 col-md-1"></div>

                        <div class="col-sm-5 col-md-5" style={{ backgroundColor: "white", opacity: 1, filter: "Alpha(opacity=50)", borderRadius: '10px' }}>

                            <h1>
                                Book homes, hotels, and more on Airbnb
                                </h1>

                            <form >
                                <div class="row" >

                                    <div class="col-sm-12 col-md-12">
                                        <br />
                                        <div class="form-group">
                                            <label for="where"><h5>WHERE</h5></label>
                                            <input type="text" onChange = {this.ChangeHandler} class="form-control" placeholder="Anywhere" name="location" id="location" />

                                        </div>

                                    </div>
                                    <div class="col-sm-1 col-md-1">

                                    </div>

                                </div>
                                <div class="row" >

                                    <div class="col-sm-6 col-md-6">

                                        <div class="col-sm-8 col-md-8">
                                            <label for="where"><h5>Check-IN</h5></label>
                                            <input onChange = {this.ChangeHandler} type="date" name="startDate" id="startDate" class="form-control js-Date"/>
                                            <i class="icon-calendar form-control-icon" aria-hidden="true">
                                            </i>
                                        </div>

                                    </div>
                                    
                                    <div class="col-sm-6 col-md-6">

                                        <div class="col-sm-8 col-md-8">
                                            <label for="where"><h5>Check-OUT</h5></label>
                                            <input onChange = {this.ChangeHandler} type="date" name="endDate" id="endDate" class="form-control js-Date"/>
                                        </div>
                                        
                                    </div>
                       

                                </div>
                                <br/>

                                 <div class="row" >

                                    <div class="col-sm-6 col-md-6">

                                        <div class="form-group">
                                            <label for="where"><h5>Sharing Type</h5></label>
                                            <div class="form-group">

                                                <select class="form-control" sharingType={this.state.sharingType} onChange = {this.handleChange} >
                                                    <option sharingType="entirePlace">Entire Place</option>
                                                    <option sharingType="privateRoom">Private Room</option>
                                                </select>
                                                
                                            </div>

                                        </div>

                                    </div>
                                    <div class="col-sm-6 col-md-6">

                                        <div class="form-group">
                                            <label for="where"><h5>Property Type</h5></label>
                                            <div class="form-group">

                                                <select class="form-control" id="propertyType">
                                                    <option>House</option>
                                                    <option>Townhouse</option>
                                                    <option>Apartment</option>
                                                </select>

                                            </div>

                                        </div>
                                    </div>

                                </div>


                                <div class="row" >
                                    <div class="col-sm-6 col-md-6">
                                        <div class="form-group">
                                         <label for="where"><h5>Price Range</h5></label>
                                            <div class="form-group">
                                                <select class="form-control" id="priceRange">
                                                    <option>100 to 1000</option>
                                                    <option>1001 to 2000</option>
                                                    <option>2001 to 3000</option>
                                                </select>
                                            </div>
                                        </div>
                                    </div>

                                    <div class="col-sm-6 col-md-6">
                                        <div class="form-group">
                                         <label for="where"><h5>Wifi Required</h5></label>
                                            <div class="form-group">
                                                <select class="form-control" id="wifi">
                                                    <option>Yes</option>
                                                    <option>No</option>
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </div>


                                <div class="row" >
                                    <div class="col-sm-12 col-md-12">
                                        <br />
